<script>
import axios from 'axios'

export default {
  name: 'Player',
  props: {},
  data () {
    return {
      stream_id : 0, ss: '',
      stream_list: {football:{count:0},basketball:{count:0},tennis:{count:0},ice_hockey:{count:0},table_tennis:{count:0},volleyball:{count:0},handball:{count:0},bandy:{count:0},snooker:{count:0},billiards:{count:0},badminton:{count:0},cricket:{count:0},golf:{count:0},bicycle:{count:0},boxing:{count:0},formula_1:{count:0},rugby:{count:0},hurling:{count:0},gaelic_football:{count:0},floorball:{count:0},motorsport:{count:0},american_football:{count:0},australian_rules:{count:0},darts:{count:0},futsal:{count:0},ufc:{count:0},baseball:{count:0},martial_arts:{count:0},horse_racing:{count:0}, sports_channels:{count:0}},
      selected_sport_type: 'football',live_sport_count: 0, current:{}, activeIndex: 'football' , show_count : 5, temp : true, first_time: true
    }
  },

  methods: {
    getEventList(){
      var that = this

      axios.get('https://api-demo.player-us.xyz/stream-list-v2/?tv=usa')
        .then(function (response) {
          that.stream_list = response.data.sports;
          that.live_sport_count = 0;
          Object.keys(that.stream_list).forEach(function (item) {
            if( that.stream_list[item].count > 0 ) {
              that.live_sport_count++;
            }            
          });

          if(that.stream_id < 1){

            if(typeof that.stream_list.football != 'undefined' && that.stream_list.football.count > 0) {      
                // setting start autopaly event  
                that.stream_id = Object.keys(that.stream_list.football.events)[0];
                that.selected_sport_type = 'football';
                that.current=that.stream_list.football;
            } else if(typeof that.stream_list.basketball != 'undefined' && that.stream_list.basketball.count > 0) {  
                
                // setting start autopaly event
                that.stream_id = Object.keys(that.stream_list.basketball.events)[0];
                that.selected_sport_type = 'basketball';
                that.current=that.stream_list.basketball;
                that.activeIndex = 'basketball';
            }
            
          }          
        })
        .catch(function (error) {
          // eslint-disable-next-line
          console.log(error)
        });
    },
    change_sport(name){
      var that=this;
      that.current=that.stream_list[name];
      that.activeIndex = name;
      that.show_all_btn;
    },
    show_all(){
      var that = this
      if(that.temp) that.temp=false;
      else that.temp=true;
      var ul_tag=document.getElementById('sport_list');
      var sport_lists=ul_tag.querySelectorAll('li');
      if(!that.temp) {
        for(let i=0;i<sport_lists.length;i++){
          if(i%2 == 0) {
            sport_lists[i].style.display="block";
            that.first_time = false;
          }
        }
      }else{
        for(let i=(that.show_count*2-2);i<sport_lists.length;i++){
          sport_lists[i].style.display="none";
          that.first_time = false;
        }
      }
      
    },
    responsive() {
      let w = window.innerWidth;
      // eslint-disable-next-line
      let h = window.innerHeight;
      let that = this;
      if( w >= 728 ) {
        document.getElementById('player').style.minWidth = "720px";
        document.getElementById('player').style.minHeight = "400px";
        that.show_count = 10;
        that.show_all_btn;
        that.first_time = true;
        that.$forceUpdate();
        //console.log(that.show_count);
      }
      else {
        document.getElementById('player').style.minWidth = (w-28) + "px";
        document.getElementById('player').style.minHeight = (w-28)/1.8 + "px";
        that.show_count = (w / 60).toFixed(0) - 1;
        that.show_all_btn;
        that.first_time = true;
        //console.log(that.show_count);
        that.$forceUpdate();
      }
    },
    show_all_btn() {
        let that = this;
        
        let show_elems = document.querySelectorAll('#sport_list li');
        // eslint-disable-next-line
        [].forEach.call(show_elems, function(item,index) {
            item.style.display = 'block';

        });

        let show_alls = document.querySelectorAll('.show_all');
        
        // eslint-disable-next-line
        [].forEach.call(show_alls, function(item,index) {
            item.style.display = 'block';
        });

        [].forEach.call(show_alls, function(item,index) {
          if( index !== (that.show_count-2) ) {
            item.style.display = 'none';
          }

        });

        let hide_count = that.show_count*2-1;
        let hide_elems = document.querySelectorAll('#sport_list li:nth-child(n+'+hide_count+')');

        // eslint-disable-next-line
        [].forEach.call(hide_elems, function(item,index) {
            item.style.display = 'none';
        });
    }
  },

  created(){
    var that = this
    // get live events list every 60 seconds and update
    that.getEventList()
    setInterval(function(){ 
      that.getEventList()
    }, 60000);
  },
  ready: function () {
    var that = this
    window.addEventListener('resize', that.responsive)    
  },
  beforeDestroy: function () {
    var that = this
    window.removeEventListener('resize', that.responsive)
  },

  updated(){
    var that = this
    window.addEventListener('resize', that.responsive)
    // eslint-disable-next-line
    let w = window.innerWidth;
    // eslint-disable-next-line
    let h = window.innerHeight;
    if( w > 728 ) {
      document.getElementById('player').style.minWidth = "720px";
      document.getElementById('player').style.minHeight = "400px";
      that.show_count = 10;

      let li_tags = document.querySelectorAll('#sport-links ul li');
      // eslint-disable-next-line
      [].forEach.call(li_tags, function(item,index) {
          item.style.width = '71.5px';
      });
    }
    else {
      document.getElementById('player').style.minWidth = (w-28) + "px";
      document.getElementById('player').style.minHeight = (w-28)/1.8 + "px";
      that.show_count = (w / 60).toFixed(0) - 1;

      let li_tags = document.querySelectorAll('#sport-links ul li');
      // eslint-disable-next-line
      [].forEach.call(li_tags, function(item,index) {
          let wd = (w-that.show_count*2-18) / that.show_count;
          item.style.width = wd+'px';
      });
    }
    if(that.first_time) {
      that.show_all_btn();
    }

  }
}
</script>

